package com.zhang.sca.common.exception;/** * 业务错误码封装 * * arch-suggestion * 业务相关的错误码，需要根据业务线，子系统划分区间，推荐使用三段式十位字符串错误码。 * 如错误码1010001000，10表示某业务线，1000表示此业务线内部的子系统，1000表示子系统内部的错误码； * * @author PengXianglong * @date 2021-04-07 10:29 */public enum BizErrorCode {    ORDER_NOT_EXIST("1010001000", "order of this ID does not exist"),    ORDER_FORBIDDEN("1010001001", "order view forbidden"),    INVALID_WAREHOUSE_CODE("1010002001", "warehouse code invalid"),    PARAM_ERROR("1000","入参错误，请检查入参"),    PARAM_ERROR_CODE("1000", "参数错误"),    SYSTEM_ERROR_CODE("1000", "系统内部错误"),    REPETITIVE_OPERATION("1010", "重复处理"),    LOGIN_ERROR("1111","登录过期请重新登录"),    USER_NOT_EXISTS("1100","用户不存在"),    ARUGMENT_ERROR("10001001", "参数不正确")    ;    private String code;    private String message;    BizErrorCode(String code, String message) {        this.code = code;        this.message = message;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }}